curl 'https://api.fly.io/graphql' --silent -H 'Accept-Encoding: gzip, deflate, br' -H 'Content-Type: application/json' -H 'Accept: application/json' -H 'Connection: keep-alive' -H 'DNT: 1' -H 'Origin: https://api.fly.io' -H 'Fly-GraphQL-Client: playground' --data-binary '{"query":"{nearestRegion{code}}  "}' --compressed | jq .data.nearestRegion.code | xargs -I {} echo "your nearest region is '{}'"